export default class TeamSection extends React.Component {
constructor(){
  super()
  this.state = {
    team: [],
    action: "",
    id_team: "",
    nama: "",
    imgSrc: "",
    jabatan: "",
    social_media: "",
    uploadFile: true,
  }
}

getTeam = () => {
  let url = "http://localhost:5000/team"
  axios.get(url)
  .then(response=> {
      this.setState({team: response.data.team})
  })
  .catch(error => {
    console.log(error);
  });
}

componentDidMount(){
  this.getTeam()
}

 render (){
  return (
    <section>
      <Container>
        <SectionHeader
          slogan="our team"
          title="The most qualified and talented individuals"
        />

        <Grid sx={styles.grid}>
          { this.state.team.map(item => (
            <TeamCard
              key={`team--key${item.id_team}`}
              src={ team_image_url + "/"+ item.imgSrc}
              // altText={item.altText}
              title={item.nama}
              designation={item.jabatan}
              // social={item.socialProfile}
            />
          ))}
        </Grid>
      </Container>
    </section>
  );
}
}

